CREATE OR REPLACE FUNCTION "airtable"."get_records"("config_name" "text", "fields" "text"[] DEFAULT NULL::"text"[]) RETURNS TABLE("id" "uuid", "airtable_record_id" "text", "data" "jsonb", "created_at" timestamp with time zone, "updated_at" timestamp with time zone)
    LANGUAGE "plpgsql"
    AS $_$
BEGIN
  IF fields IS NULL THEN
    RETURN QUERY
      SELECT 
        r.id, 
        r.airtable_record_id, 
        r.data, 
        r.created_at, 
        r.updated_at
      FROM airtable.records r
      JOIN airtable.sync_configs c ON c.id = r.sync_config_id
      WHERE c.table_name = config_name;
  ELSE
    RETURN QUERY
      SELECT 
        r.id, 
        r.airtable_record_id, 
        jsonb_path_query_array(r.data, '$.' || array_to_string(fields, ', $.')) AS data, 
        r.created_at, 
        r.updated_at
      FROM airtable.records r
      JOIN airtable.sync_configs c ON c.id = r.sync_config_id
      WHERE c.table_name = config_name;
  END IF;
END;
$_$;


