CREATE OR REPLACE FUNCTION "airtable"."queue_sync"("config_id" integer DEFAULT NULL::integer) RETURNS integer
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public'
    AS $$
DECLARE
  inserted_id INTEGER;
BEGIN
  IF config_id IS NULL THEN
    -- Queue all enabled configs
    INSERT INTO airtable.sync_requests (config_id)
    SELECT id FROM airtable.sync_configs WHERE enabled = true
    RETURNING id INTO inserted_id;
  ELSE
    -- Queue specific config
    INSERT INTO airtable.sync_requests (config_id)
    VALUES (config_id)
    RETURNING id INTO inserted_id;
  END IF;
  
  RETURN inserted_id;
END;
$$;


