CREATE OR REPLACE FUNCTION "public"."normalize_phone"("raw" "text", "default_country" "text" DEFAULT 'US'::"text") RETURNS "text"
    LANGUAGE "plpgsql" IMMUTABLE
    AS $_$
DECLARE
  cleaned text;
  digits  text;
BEGIN
  IF raw IS NULL OR btrim(raw) = '' THEN
    RETURN NULL;
  END IF;

  -- Strip common extensions: ext 123, ext. 123, x123, #123
  cleaned := regexp_replace(raw, '(?i)\s*(?:ext\.?|x|#)\s*[0-9]+\s*$', '', 'g');

  -- If it starts with '+', keep only '+' and digits and validate E.164
  IF cleaned ~ '^\s*\+' THEN
    cleaned := regexp_replace(cleaned, '[^0-9+]', '', 'g');
    IF cleaned ~ '^\+[1-9][0-9]{7,14}$' THEN
      RETURN cleaned;
    ELSE
      RETURN NULL;
    END IF;
  END IF;

  -- Otherwise, reduce to digits and apply US heuristics
  digits := regexp_replace(cleaned, '\D', '', 'g');

  IF default_country = 'US' THEN
    IF length(digits) = 10 THEN
      RETURN '+1' || digits;
    ELSIF length(digits) = 11 AND digits LIKE '1%' THEN
      RETURN '+' || digits;
    ELSE
      RETURN NULL;
    END IF;
  ELSE
    RETURN NULL;
  END IF;
END;
$_$;


