CREATE OR REPLACE FUNCTION "util"."component"("components" "jsonb", "wanted" "text", "use_short" boolean DEFAULT false) RETURNS "text"
    LANGUAGE "sql" STABLE
    AS $$                                                                           
  select case when use_short then c->>'short_name' else c->>'long_name' end     
  from jsonb_array_elements(components) c                                       
  where exists (                                                                
    select 1                                                                    
    from jsonb_array_elements_text(c->'types') t                                
    where t = wanted                                                            
  )                                                                             
  limit 1                                                                       
$$;


