CREATE OR REPLACE FUNCTION "util"."geocode_address"("addr" "text") RETURNS "util"."geocode_result"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'extensions', 'util', 'vault'
    AS $$                                                                           
declare                                                                         
  api_key text;                                                                 
  url text;                                                                     
  resp extensions.http_response;                                                
  body jsonb;                                                                   
  result util.geocode_result;                                                   
begin                                                                           
  if addr is null or length(trim(addr)) = 0 then                                
    return null;                                                                
  end if;                                                                       
                                                                                
  begin                                                                         
    api_key := Deno.env.get('GOOGLE_MAPS_API_KEY');                         
  exception when others then                                                    
    api_key := null;
    raise exception 'Google Maps API key not found';                                                           
  end;                                                                                                                                                                                                     
  -- Build URL (basic space->+ replacement; adjust if you need full encoding)   
  url := 'https://maps.googleapis.com/maps/api/geocode/json?address='           
         || replace(addr, ' ', '+')                                             
         || '&key=' || api_key;                                                 
                                                                                
  resp := extensions.http_get(url, extensions.http_headers('accept',            
'application/json'));                                                           
                                                                                
  if resp.status <> 200 then                                                    
    raise exception 'Geocode HTTP error: % %', resp.status,                     
left(coalesce(resp.content, ''), 200);                                          
  end if;                                                                       
                                                                                
  body := resp.content::jsonb;                                                  
                                                                                
  if (body->>'status') not in ('OK') then                                       
    -- propagate Google status (ZERO_RESULTS, OVER_QUERY_LIMIT, etc.)           
    raise exception 'Geocode API status: %', body->>'status';                   
  end if;                                                                       
                                                                                
  result.lat :=                                                                 
((body->'results'->0->'geometry'->'location'->>'lat')::numeric)::float8;        
  result.lng :=                                                                 
((body->'results'->0->'geometry'->'location'->>'lng')::numeric)::float8;        
  result.formatted_address := body->'results'->0->>'formatted_address';         
  result.place_id := body->'results'->0->>'place_id';                           
  result.raw := body;                                                           
                                                                                
  return result;                                                                
end                                                                             
$$;


ALTER FUNCTION "util"."geocode_address"("addr" "text") OWNER TO "postgres";


CREATE OR REPLACE FUNCTION "util"."trg_action_steps_status_completed_date"() RETURNS "trigger"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'util'
    AS $$                                                                           
declare                                                                         
  now_ts timestamptz := now();                                                  
  is_complete boolean;                                                          
  was_complete boolean := false;                                                
  toggled_to_complete boolean := false;                                         
  date_changed boolean := false;                                                
begin                                                                           
  -- Normalize current status                                                   
  is_complete := lower(coalesce(new.status, '')) = 'complete';                  
                                                                                
  -- Change detection                                                           
  if tg_op = 'INSERT' then                                                      
    toggled_to_complete := is_complete;                                         
    date_changed := new.completed_date is not null;                             
  else                                                                          
    was_complete := lower(coalesce(old.item_status, '')) = 'complete'::action_step_status;               
    toggled_to_complete := (is_complete is distinct from was_complete) and      
is_complete = true;                                                             
    date_changed := new.completed_date is distinct from old.completed_date;     
  end if;                                                                       
                                                                                
  -- Rule A: if completed_date is filled in, ensure status = 'Complete'         
  if new.completed_date is not null and (tg_op = 'INSERT' or date_changed) then 
    if is_complete = false then                                                 
      new.item_status := 'Complete'::action_step_status ;  -- If enum, cast: 'Complete'::your_enum        
      is_complete := true;                                                      
    end if;                                                                     
  end if;                                                                       
                                                                                
  -- Rule B: when marking status to 'Complete'                                  
  if toggled_to_complete then                                                   
    if new.completed_date is null then                                          
      -- Auto-fill completed_date if empty                                      
      new.completed_date := now_ts;                                             
    else                                                                        
                                                        
      if tg_op = 'UPDATE' and not date_changed then                             
        insert into public.action_step_prompts (action_step_id, field_name,     
old_value, proposed_value)                                                      
        values (new.id, 'completed_date', new.completed_date, now_ts)           
        on conflict (action_step_id, field_name) where item_status::text = 'pending' do    
nothing;                                                                        
      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  return new;                                                                   
end                                                                             
$$;


