CREATE OR REPLACE FUNCTION "util"."trg_geocode_on_write"() RETURNS "trigger"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'util'
    AS $$                                                                           
declare                                                                         
  g util.geocode_result;                                                        
  addr text;                                                                    
  comps jsonb;                                                                  
  street_number text;                                                           
  route_name text;                                                              
  city_name text;                                                               
  state_code text;                                                              
  postal_code text;                                                             
  country_code text;                                                            
begin                                                                           
  -- Build/choose the input address (single column here; change if needed)      
  addr := coalesce(new.address,new.home_address);                                                          
                                                                                
  if addr is null or length(trim(addr)) = 0 then                                
    return new;                                                                 
  end if;                                                                       
                                                                                
  -- Only geocode if the address changed (or on insert)                         
  if tg_op = 'UPDATE' then                                                      
    if new.address is not distinct from old.address then                        
      return new;                                                               
    end if;                                                                     
  end if;                                                                       
                                                                                
  begin                                                                         
    -- Call the existing geocoder (util.geocode_address)                        
    select * into g from util.geocode_address(addr);                            
                                                                                
    -- Coordinates                                                              
    new.lat := g.lat;                                                           
    new.long := g.lng;                                                           
                                                                                
    -- Extract address components from Google response                          
    comps := g.raw->'results'->0->'address_components';                         
                                                                                
    street_number := util.component(comps, 'street_number');                    
    route_name    := util.component(comps, 'route');                            
    city_name     := coalesce(                                                  
                       util.component(comps, 'locality'),                       
                       util.component(comps, 'postal_town'),                    
                       util.component(comps, 'sublocality'),                    
                       util.component(comps, 'administrative_area_level_2')     
                     );                                                         
    state_code    := util.component(comps, 'administrative_area_level_1', true);
    postal_code   := util.component(comps, 'postal_code');                      
    country_code  := util.component(comps, 'country', true); -- e.g., 'US'      
                                                                                
    -- Assign into your table columns (rename here if yours differ)             
    new.street  := btrim(coalesce(street_number, '') || ' ' || coalesce(route_name,''));                                                       
    new.city    := city_name;                                                   
    new.state   := state_code;                                                  
    new.zip     := postal_code;                                                 
    new.country := country_code;                                                
                                                                                
    new.geocoded_at := now();                                     
  end;                                                                          
                                                                                
  return new;                                                                   
end                                                                             
$$;


ALTER FUNCTION "util"."trg_geocode_on_write"() OWNER TO "postgres";


CREATE OR REPLACE FUNCTION "util"."trg_locations_current_and_enddate"() RETURNS "trigger"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'util'
    AS $$                                                                           
declare                                                                         
  now_ts timestamptz := now();                                                  
  end_changed boolean := false;                                                 
  any_current_false boolean := false;                                           
  toggled_to_false boolean := false;                                            
begin                                                                           
  -- Did end_date change in this statement?                                     
  end_changed := (tg_op = 'INSERT') or (new.end_date is distinct from           
old.end_date);                                                                  
                                                                                
  -- Rule 1: if an end date is entered, mark both current flags false           
  if new.end_date is not null and end_changed then                              
    new.current_mail_address := false;                                          
    new.current_physical_address := false;                                      
  end if;                                                                       
                                                                                
  -- Compute “any current is false” and “toggled to false”                      
  any_current_false := coalesce(new.current_mail_address = false, false)        
                       or coalesce(new.current_physical_address = false, false);                                                                                
  if tg_op = 'INSERT' then                                                      
    toggled_to_false := any_current_false;                                      
  else                                                                          
    toggled_to_false :=                                                         
      (old.current_mail_address is distinct from new.current_mail_address and   
new.current_mail_address = false)                                               
      or                                                                        
      (old.current_physical_address is distinct from                            
new.current_physical_address and new.current_physical_address = false);         
  end if;                                                                       
                                                                                
  -- Rule 2: if either current flag is false                                    
  if any_current_false then                                                     
    if new.end_date is null then                                                
      -- Auto-fill end_date when missing                                        
      new.end_date := now_ts;                                                   
    else                                                                        
      -- end_date already set: prompt only if it wasn't changed in this same 
      -- statement                                                                       
      if toggled_to_false and not end_changed then                              
        insert into public.location_prompts (location_id, field_name, old_value,
proposed_value)                                                                 
        values (new.id, 'end_date', new.end_date, now_ts)                       
        on conflict (location_id, field_name) where status = 'pending' do       
nothing;                                                                        
      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  return new;                                                                   
end                                                                             
$$;


