    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'util'
    AS $$                                                                           
declare                                                                         
  status_norm text;                                                             
  now_ts timestamptz := now();                                                  
begin                                                                           
  -- Only act when status is set or changes                                     
  if tg_op = 'UPDATE' then                                                      
    if new.status is not distinct from old.status then                          
      return new;                                                               
    end if;                                                                     
  end if;                                                                       
                                                                                
  status_norm := lower(trim(new.status));                                       
                                                                                
  -- OPEN -> opened                                                             
  if status_norm = 'open' then                                                  
    if new.opened is null then                                                  
      new.opened := now_ts;                                                     
    else                                                                        
      -- If user didn't change opened in this same statement, prompt            
      if tg_op = 'INSERT' or new.opened is not distinct from old.opened then    
        insert into public.school_status_prompts (school_id, field_name,        
old_value, proposed_value)                                                      
        values (new.id, 'opened', new.opened, now_ts)                           
        on conflict (school_id, field_name) where status = 'pending' do nothing;      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  -- STARTUP -> entered_startup_date                                            
  if status_norm = 'startup' then                                               
    if new.entered_startup_date is null then                                    
      new.entered_startup_date := now_ts;                                       
    else                                                                        
      if tg_op = 'INSERT' or new.entered_startup_date is not distinct from      
old.entered_startup_date then                                                   
        insert into public.school_status_prompts (school_id, field_name,        
old_value, proposed_value)                                                      
        values (new.id, 'entered_startup_date', new.entered_startup_date,       
now_ts)                                                                         
        on conflict (school_id, field_name) where status = 'pending' do nothing;      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  -- PLANNING -> entered_planning_date                                          
  if status_norm = 'planning' then                                              
    if new.entered_planning_date is null then                                   
      new.entered_planning_date := now_ts;                                      
    else                                                                        
      if tg_op = 'INSERT' or new.entered_planning_date is not distinct from     
old.entered_planning_date then                                                  
        insert into public.school_status_prompts (school_id, field_name,        
old_value, proposed_value)                                                      
        values (new.id, 'entered_planning_date', new.entered_planning_date,     
now_ts)                                                                         
        on conflict (school_id, field_name) where status = 'pending' do nothing;
      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  -- VISIONING -> entered_visioning_date                                        
  if status_norm = 'visioning' then                                             
    if new.entered_visioning_date is null then                                  
      new.entered_visioning_date := now_ts;                                     
    else                                                                        
      if tg_op = 'INSERT' or new.entered_visioning_date is not distinct from    
old.entered_visioning_date then                                                 
        insert into public.school_status_prompts (school_id, field_name,        
old_value, proposed_value)                                                      
        values (new.id, 'entered_visioning_date', new.entered_visioning_date,   
now_ts)                                                                         
        on conflict (school_id, field_name) where status = 'pending' do nothing;
      end if;                                                                   
    end if;                                                                     
  end if;                                                                       
                                                                                
  return new;                                                                   
end                                                                             
$$;


