CREATE OR REPLACE VIEW "public"."grid_educator" AS
 SELECT "p"."id",
    "p"."full_name",
    "roa"."current_role_at_active_school",
    "roa"."current_role",
    "roa"."active_school",
    (EXISTS ( SELECT 1
           FROM "public"."montessori_certs" "mc"
          WHERE ("mc"."people_id" = "p"."id"))) AS "has_montessori_cert",
    "public"."map_race_ethnicity"("p"."race_ethnicity") AS "race_ethnicity_display",
    ("peec"."discovery_status")::"text" AS "discovery_status",
    "p"."indiv_type",
        CASE
            WHEN "st"."any_paused" THEN 'paused'::"text"
            WHEN "st"."any_open" THEN 'open'::"text"
            WHEN "st"."any_year1" THEN 'year 1'::"text"
            WHEN "st"."any_startup" THEN 'startup'::"text"
            WHEN "st"."any_planning" THEN 'planning'::"text"
            WHEN "st"."any_visioning" THEN 'visioning'::"text"
            WHEN (COALESCE(("peec"."discovery_status")::"text", ''::"text") ~~* 'complete%'::"text") THEN 'discovery_complete'::"text"
            WHEN (COALESCE(("peec"."discovery_status")::"text", ''::"text") ~~* 'in process%'::"text") THEN 'discovery_in_process'::"text"
            WHEN "f"."any_1on1_completed" THEN '1:1 completed'::"text"
            WHEN "f"."any_1on1_scheduled" THEN '1:1 scheduled'::"text"
            WHEN "f"."any_outreach_sent" THEN 'outreach_sent'::"text"
            WHEN "f"."any_partner_assigned" THEN 'partner_assigned'::"text"
            WHEN "f"."any_inquiry" THEN 'inquiry_received'::"text"
            ELSE 'no_inquiry_received'::"text"
        END AS "kanban_group",
        CASE
            WHEN "st"."any_paused" THEN 14
            WHEN "st"."any_open" THEN 13
            WHEN "st"."any_year1" THEN 12
            WHEN "st"."any_startup" THEN 11
            WHEN "st"."any_planning" THEN 10
            WHEN "st"."any_visioning" THEN 9
            WHEN (COALESCE(("peec"."discovery_status")::"text", ''::"text") ~~* 'complete%'::"text") THEN 8
            WHEN (COALESCE(("peec"."discovery_status")::"text", ''::"text") ~~* 'in process%'::"text") THEN 7
            WHEN "f"."any_1on1_completed" THEN 6
            WHEN "f"."any_1on1_scheduled" THEN 5
            WHEN "f"."any_outreach_sent" THEN 4
            WHEN "f"."any_partner_assigned" THEN 3
            WHEN "f"."any_inquiry" THEN 2
            ELSE 1
        END AS "kanban_order"
   FROM (((("public"."people" "p"
     LEFT JOIN "public"."people_educator_early_cultivation" "peec" ON (("peec"."people_id" = "p"."id")))
     LEFT JOIN LATERAL ( SELECT "string_agg"("x"."s", ', '::"text" ORDER BY "x"."s") AS "current_role_at_active_school",
            "string_agg"(DISTINCT "x"."role", ', '::"text" ORDER BY "x"."role") AS "current_role",
            "string_agg"(DISTINCT "x"."school_name", ', '::"text" ORDER BY "x"."school_name") AS "active_school"
           FROM ( SELECT DISTINCT "pra"."role",
                    COALESCE("s"."short_name", "s"."long_name", "s"."legal_name", ''::"text") AS "school_name",
                    "concat"("pra"."role", ' at ', COALESCE("s"."short_name", "s"."long_name", "s"."legal_name", ''::"text"), ' (',
                        CASE
                            WHEN (("s"."status")::"text" = 'Emerging'::"text") THEN COALESCE(("ssj"."ssj_stage")::"text", 'Emerging'::"text")
                            WHEN (("s"."status")::"text" = 'Paused'::"text") THEN 'Paused'::"text"
                            ELSE ("s"."status")::"text"
                        END, ')') AS "s"
                   FROM ((("public"."people_roles_associations" "pra"
                     JOIN "public"."ref_roles" "rls" ON ((("rls"."role_short" = "pra"."role") AND ("rls"."show_in_educator_grid" IS TRUE))))
                     LEFT JOIN "public"."schools" "s" ON (("s"."id" = "pra"."school_id")))
                     LEFT JOIN "public"."school_ssj_data" "ssj" ON (("ssj"."school_id" = "s"."id")))
                  WHERE (("pra"."people_id" = "p"."id") AND ("pra"."currently_active" IS TRUE))) "x") "roa" ON (true))
     LEFT JOIN LATERAL ( SELECT "bool_or"((("s"."status")::"text" = 'Paused'::"text")) AS "any_paused",
            "bool_or"((("s"."status")::"text" = 'Open'::"text")) AS "any_open",
            "bool_or"((("s"."status")::"text" = 'Year 1'::"text")) AS "any_year1",
            "bool_or"(((("s"."status")::"text" = 'Emerging'::"text") AND (("ssj"."ssj_stage")::"text" = 'Startup'::"text"))) AS "any_startup",
            "bool_or"(((("s"."status")::"text" = 'Emerging'::"text") AND (("ssj"."ssj_stage")::"text" = 'Planning'::"text"))) AS "any_planning",
            "bool_or"(((("s"."status")::"text" = 'Emerging'::"text") AND (("ssj"."ssj_stage")::"text" = 'Visioning'::"text"))) AS "any_visioning"
           FROM ((("public"."people_roles_associations" "pra"
             JOIN "public"."ref_roles" "rls" ON ((("rls"."role_short" = "pra"."role") AND ("rls"."show_in_educator_grid" IS TRUE))))
             LEFT JOIN "public"."schools" "s" ON (("s"."id" = "pra"."school_id")))
             LEFT JOIN "public"."school_ssj_data" "ssj" ON (("ssj"."school_id" = "s"."id")))
          WHERE (("pra"."people_id" = "p"."id") AND ("pra"."currently_active" IS TRUE))) "st" ON (true))
     LEFT JOIN LATERAL ( SELECT "bool_or"((COALESCE("f_1"."one_on_one_status", ''::"text") ~~* 'Completed%'::"text")) AS "any_1on1_completed",
            "bool_or"((COALESCE("f_1"."one_on_one_status", ''::"text") ~~* 'Scheduled%'::"text")) AS "any_1on1_scheduled",
            "bool_or"((COALESCE("f_1"."email_sent_by_initial_outreacher", ''::"text") <> ''::"text")) AS "any_outreach_sent",
            "bool_or"((COALESCE("f_1"."routed_to", ''::"text") <> ''::"text")) AS "any_partner_assigned",
            ("count"(*) > 0) AS "any_inquiry"
           FROM "public"."ssj_fillout_forms" "f_1"
          WHERE ("f_1"."people_id" = "p"."id")) "f" ON (true));


