CREATE OR REPLACE VIEW "public"."grid_school" AS
 SELECT "s"."id",
    COALESCE("s"."short_name", "s"."long_name") AS "school_name",
        CASE
            WHEN (("s"."status")::"text" = 'Emerging'::"text") THEN ("ssj"."ssj_stage")::"text"
            WHEN (("s"."status")::"text" = 'Paused'::"text") THEN 'Paused'::"text"
            ELSE ("s"."status")::"text"
        END AS "stage_status",
    ( SELECT "string_agg"(DISTINCT "p2"."full_name", ', '::"text" ORDER BY "p2"."full_name") AS "string_agg"
           FROM ("public"."people_roles_associations" "pra2"
             JOIN "public"."people" "p2" ON (("p2"."id" = "pra2"."people_id")))
