         SELECT "email_candidates"."people_id",
            "email_candidates"."email_address",
            "email_candidates"."source",
            "email_candidates"."category",
            "email_candidates"."priority",
            "row_number"() OVER (PARTITION BY "email_candidates"."people_id" ORDER BY "email_candidates"."priority") AS "rn"
           FROM "email_candidates"
        )
 SELECT "ranked_emails"."people_id",
