    "ssj"."ssj_tool",
    "ssj"."ssj_building4good_status",
    "ssj"."building4good_firm_and_attorney",
    "ssj"."visioning_album_complete",
    "ssj"."visioning_album",
    "ssj"."logo_designer",
    "ssj"."name_selection_proposal",
    "ssj"."trademark_filed",
    "ssj"."ssj_ops_guide_support_track",
    "m"."membership_status_after_action" AS "membership_status",
    "odr"."proj_open_date" AS "projected_open",
    "loc_phys"."address" AS "physical_address",
    "loc_phys"."lat" AS "physical_lat",
    "loc_phys"."long" AS "physical_long",
    "loc_mail"."address" AS "mailing_address",
    "coh"."cohort_name" AS "current_cohort",
    "ga"."current_guide_name",
    "gsum"."total_grants_issued",
    "lsum"."total_loans_issued"
   FROM ((((((((("public"."schools" "s"
     LEFT JOIN "public"."school_ssj_data" "ssj" ON (("ssj"."school_id" = "s"."id")))
     LEFT JOIN LATERAL ( SELECT "ma"."membership_status_after_action",
            "ma"."action_date" AS "date"
           FROM "public"."membership_actions" "ma"
          WHERE ("ma"."school_id" = "s"."id")
          ORDER BY "ma"."action_date" DESC NULLS LAST,
                CASE
                    WHEN ("ma"."action" = 'Withdrew from Network'::"public"."membership_action_options") THEN 2
                    WHEN ("ma"."action" = 'Signed Membership Agreement'::"public"."membership_action_options") THEN 1
                    ELSE 0
                END DESC, "ma"."id" DESC
         LIMIT 1) "m" ON (true))
     LEFT JOIN LATERAL ( SELECT "od"."proj_open_date"
           FROM "public"."open_date_revisions" "od"
          WHERE ("od"."school_id" = "s"."id")
          ORDER BY "od"."created_at" DESC NULLS LAST
         LIMIT 1) "odr" ON (true))
     LEFT JOIN LATERAL ( SELECT "l"."address",
            "l"."lat",
            "l"."long"
           FROM "public"."locations" "l"
          WHERE (("l"."school_id" = "s"."id") AND ("l"."current_physical_address" = true))
         LIMIT 1) "loc_phys" ON (true))
     LEFT JOIN LATERAL ( SELECT "l"."address",
            "l"."lat",
            "l"."long"
           FROM "public"."locations" "l"
          WHERE (("l"."school_id" = "s"."id") AND (COALESCE("l"."current_mail_address", false) = true))
         LIMIT 1) "loc_mail" ON (true))
     LEFT JOIN LATERAL ( SELECT "cp"."cohort" AS "cohort_name"
           FROM "public"."cohort_participation" "cp"
          WHERE (("cp"."school_id" = "s"."id") AND ("cp"."participation_status" = ANY (ARRAY['Active'::"text", 'Complete'::"text"])))
          ORDER BY "cp"."created_at" DESC NULLS LAST
         LIMIT 1) "coh" ON (true))
     LEFT JOIN LATERAL ( SELECT "g"."full_name" AS "current_guide_name"
           FROM ("public"."guide_assignments" "ga_1"
             JOIN "public"."guides" "g" ON (("g"."id" = "ga_1"."guide_id")))
          WHERE (("ga_1"."school_id" = "s"."id") AND ("ga_1"."active" = true))
          ORDER BY COALESCE("ga_1"."end_date", "ga_1"."start_date") DESC NULLS LAST, "ga_1"."id" DESC
         LIMIT 1) "ga" ON (true))
     LEFT JOIN LATERAL ( SELECT COALESCE("sum"("gr"."amount"), (0)::numeric) AS "total_grants_issued"
           FROM "public"."grants" "gr"
          WHERE (("gr"."school_id" = "s"."id") AND ("gr"."grant_status" = 'Issued'::"text"))) "gsum" ON (true))
     LEFT JOIN LATERAL ( SELECT (COALESCE("sum"("ln"."amount_issued"), (0)::bigint))::numeric AS "total_loans_issued"
           FROM "public"."loans" "ln"
          WHERE (("ln"."school_id" = "s"."id") AND ("ln"."issue_date" IS NOT NULL))) "lsum" ON (true));


